import * as React from 'react';
import { NixSound } from '../../types';

interface SoundIconProps extends React.HTMLAttributes<HTMLButtonElement> {
	name: NixSound;
}

export const SoundIcon: React.FC<SoundIconProps> = ({ name }: SoundIconProps) => {
	switch (name) {
		case 'fire':
			return <Fire />;
		case 'brook':
			return <Brook />;
		case 'party_chatter':
			return <PartyChatter />;
		case 'fan':
			return <Fan />;
		case 'rain_pouring':
			return <RainPouring />;
		case 'rain_calm':
			return <RainCalm />;
		case 'beach_shore':
			return <BeachShore />;
		case 'rushing_stream':
			return <RushingStream />;
		case 'thunder_nearby':
			return <ThunderNearby />;
		case 'thunder_distant':
			return <ThunderDistant />;
		case 'waves_rolling':
			return <WavesRolling />;
		case 'waves_calm':
			return <WavesCalm />;
		default:
			break;
	}

	return null;
};

